CREATE  TABLE account (ID INT(11) NOT NULL AUTO_INCREMENT, Balans DECIMAL(50) NOT NULL, State ENUM('Working','Lock') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, FK_Users INT(11) NOT NULL, PRIMARY KEY (ID), Foreign Key (FK_Users) REFERENCES users(ID)) ENGINE = InnoDB CHARACTER set UTF8 COLLATE UTF8_general_ci;