CREATE TABLE payment ( ID INT(11) NOT NULL AUTO_INCREMENT , FK_Account_Source INT(11) NOT NULL , FK_Account_Destination INT(11) NOT NULL , Description VARCHAR(200) NOT NULL , Amount DECIMAL(50) NOT NULL , Paydate DATE NOT NULL , PRIMARY KEY (ID), Foreign Key (FK_Account_Source) REFERENCES account(ID), Foreign Key (FK_Account_Destination) REFERENCES account(ID)) ENGINE = InnoDB CHARACTER set UTF8 COLLATE UTF8_general_ci;