CREATE TABLE users ( ID INT(11) NOT NULL AUTO_INCREMENT , First_Name VARCHAR(20) NOT NULL , Middle_Name VARCHAR(20) NOT NULL , Last_Name VARCHAR(20) NOT NULL , Passport VARCHAR(100) NOT NULL , Address VARCHAR(100) NOT NULL , Phone VARCHAR(13) NOT NULL, Login VARCHAR(50) NOT NULL , Password VARCHAR(50) NOT NULL , Email VARCHAR(100) NOT NULL , FK_Role INT(11) NOT NULL , PRIMARY KEY (ID), Foreign Key (FK_Role) REFERENCES role(ID)) ENGINE = InnoDB CHARACTER set UTF8 COLLATE UTF8_general_ci;